    const processedControls: Word.ContentControl[] = [];

    for (let i = 0; i < contentControls.items.length; i++) {
        const cc = contentControls.items[i];
        cc.load("tag, text");
        context.trackedObjects.add(cc); // Track for processing
        processedControls.push(cc);
