    // Do some work with the content controls
    for (const cc of processedControls) {
        if (cc.tag === "processed") {
            console.log(cc.text);
        }
    }

    // Untrack all processed content controls to free memory
    for (const cc of processedControls) {
        cc.untrack();
    }

    await context.sync();
});
