    const shapes = context.document.body.shapes;
    shapes.load("items");
    await context.sync();

    if (shapes.items.length > 0) {
        const shape = shapes.items[0];
        const threeDFormat = shape.threeDFormat;

        // Load the 3D format properties
        threeDFormat.load("*");
        await context.sync();

        // Convert to plain JavaScript object
